import React, { useState, useEffect } from 'react';
import './app.css';
import LandingPage from './LandingPage';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

// ==================== LOGIN PAGE ====================

function LoginPage({ onLogin }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isRegister, setIsRegister] = useState(false);
  const [name, setName] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    
    try {
      let response;
      if (isRegister) {
        response = await fetch(`${API_URL}/api/auth/register`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ email, password, name })
        });
      } else {
        response = await fetch(`${API_URL}/api/auth/login`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ email, password })
        });
      }
      
      const data = await response.json();
      
      if (response.ok) {
        localStorage.setItem('token', data.access_token);
        localStorage.setItem('userId', data.user_id);
        onLogin();
      } else {
        setError(data.error || (isRegister ? 'Registration failed' : 'Login failed'));
      }
    } catch (err) {
      setError(err.message || 'Network error');
    }
  };

  const handleClearStorage = () => {
    localStorage.clear();
    window.location.reload();
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h1 className="healmate-title">HealMate</h1>
        <h2>{isRegister ? 'Create Account' : 'Login'}</h2>
        
        {error && <div className="error-message">{error}</div>}
        
        <form onSubmit={handleSubmit}>
          {isRegister && (
            <input
              type="text"
              placeholder="Full Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          )}
          
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          
          <button type="submit" className="btn-primary">
            {isRegister ? 'Create Account' : 'Login'}
          </button>
        </form>
        
        <p>
          {isRegister ? 'Already have an account?' : "Don't have an account?"}{' '}
          <button
            type="button"
            className="link-btn"
            onClick={() => setIsRegister(!isRegister)}
          >
            {isRegister ? 'Login' : 'Register'}
          </button>
        </p>
        
        <button 
          type="button" 
          className="btn-reset"
          onClick={handleClearStorage}
          style={{ marginTop: '10px', fontSize: '12px' }}
        >
          Clear Cache & Reset
        </button>
      </div>
    </div>
  );
}

// ==================== PROFILE SETUP PAGE ====================

function ProfileSetupPage({ token, onComplete }) {
  const [profile, setProfile] = useState({
    nickname: '',
    height_cm: '',
    weight_kg: '',
    blood_type: '',
    blood_pressure_sys: '',
    blood_pressure_dia: '',
    blood_sugar_fasting: '',
    sleep_goal_hours: '8'
  });
  const [medications, setMedications] = useState([]);
  const [newMed, setNewMed] = useState({
    name: '',
    dosage: '',
    frequency: '',
    stock_quantity: ''
  });
  const [saving, setSaving] = useState(false);

  const handleProfileChange = (e) => {
    const { name, value } = e.target;
    setProfile(prev => ({ ...prev, [name]: value }));
  };

  const handleAddMedication = () => {
    if (newMed.name && newMed.dosage && newMed.frequency && newMed.stock_quantity) {
      setMedications(prev => [...prev, newMed]);
      setNewMed({ name: '', dosage: '', frequency: '', stock_quantity: '' });
    }
  };

  const handleRemoveMedication = (index) => {
    setMedications(prev => prev.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Check if profile has valid data
    if (!profile.nickname) {
      alert('Please enter a nickname');
      return;
    }
    
    setSaving(true);
    
    try {
      console.log('Submitting profile:', profile);
      console.log('Token:', token);
      
      const profileResponse = await fetch(`${API_URL}/api/user/profile`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(profile)
      });
      
      console.log('Profile response status:', profileResponse.status);
      const profileData = await profileResponse.json();
      console.log('Profile response data:', profileData);
      
      if (profileResponse.ok) {
        console.log('Profile saved successfully');
        
        // Save medications if any
        if (medications.length > 0) {
          console.log('Saving medications:', medications);
          for (const med of medications) {
            const medResponse = await fetch(`${API_URL}/api/medications`, {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
              },
              body: JSON.stringify(med)
            });
            console.log('Medication response status:', medResponse.status);
          }
        }
        
        localStorage.setItem('profileSetup', 'true');
        alert('Profile saved successfully! Redirecting to dashboard...');
        onComplete();
      } else {
        alert('Failed to save profile: ' + (profileData.error || 'Unknown error'));
      }
    } catch (err) {
      console.error('Profile update failed:', err);
      alert('Error saving profile: ' + err.message);
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="profile-setup-container">
      <div className="profile-setup-card">
        <h2>Complete Your Profile</h2>
        <p>Let's set up your health information</p>

        <form onSubmit={handleSubmit}>
          {/* Basic Info */}
          <div className="setup-section">
            <h3>Basic Information</h3>
            <input
              type="text"
              name="nickname"
              placeholder="Nickname"
              value={profile.nickname}
              onChange={handleProfileChange}
              required
            />
          </div>

          {/* Physical Measurements */}
          <div className="setup-section">
            <h3>Physical Measurements</h3>
            <div className="setup-row">
              <input
                type="number"
                name="height_cm"
                placeholder="Height (cm)"
                value={profile.height_cm}
                onChange={handleProfileChange}
              />
              <input
                type="number"
                name="weight_kg"
                placeholder="Weight (kg)"
                value={profile.weight_kg}
                onChange={handleProfileChange}
              />
            </div>
            <input
              type="text"
              name="blood_type"
              placeholder="Blood Type (e.g., O+)"
              value={profile.blood_type}
              onChange={handleProfileChange}
            />
          </div>

          {/* Health Readings */}
          <div className="setup-section">
            <h3>Health Readings</h3>
            <div className="setup-row">
              <input
                type="number"
                name="blood_pressure_sys"
                placeholder="BP Systolic"
                value={profile.blood_pressure_sys}
                onChange={handleProfileChange}
              />
              <input
                type="number"
                name="blood_pressure_dia"
                placeholder="BP Diastolic"
                value={profile.blood_pressure_dia}
                onChange={handleProfileChange}
              />
            </div>
            <input
              type="number"
              name="blood_sugar_fasting"
              placeholder="Fasting Blood Sugar (mg/dL)"
              value={profile.blood_sugar_fasting}
              onChange={handleProfileChange}
            />
          </div>

          {/* Sleep */}
          <div className="setup-section">
            <h3>Sleep Goal</h3>
            <input
              type="number"
              name="sleep_goal_hours"
              placeholder="Sleep Goal (hours)"
              value={profile.sleep_goal_hours}
              onChange={handleProfileChange}
            />
          </div>

          {/* Medications */}
          <div className="setup-section">
            <h3>Medications (Optional)</h3>
            <div className="med-input-group">
              <input
                type="text"
                placeholder="Medication Name"
                value={newMed.name}
                onChange={(e) => setNewMed({...newMed, name: e.target.value})}
              />
              <input
                type="text"
                placeholder="Dose (e.g., 500mg)"
                value={newMed.dosage}
                onChange={(e) => setNewMed({...newMed, dosage: e.target.value})}
              />
              <input
                type="text"
                placeholder="Frequency (e.g., twice daily)"
                value={newMed.frequency}
                onChange={(e) => setNewMed({...newMed, frequency: e.target.value})}
              />
              <input
                type="number"
                placeholder="Stock Qty"
                value={newMed.stock_quantity}
                onChange={(e) => setNewMed({...newMed, stock_quantity: e.target.value})}
              />
              <button type="button" className="btn-add-med" onClick={handleAddMedication}>
                + Add
              </button>
            </div>

            {medications.length > 0 && (
              <div className="med-list">
                {medications.map((med, idx) => (
                  <div key={idx} className="med-item">
                    <span>{med.name} - {med.dosage}, {med.frequency}</span>
                    <button 
                      type="button" 
                      onClick={() => handleRemoveMedication(idx)}
                      className="btn-remove"
                    >
                      âœ•
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>

          <button type="submit" className="btn-complete" disabled={saving}>
            {saving ? 'Saving...' : 'Complete Setup'}
          </button>
        </form>
      </div>
    </div>
  );
}

// ==================== CHAT PAGE ====================

function ChatPage({ token }) {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    // Load chat history on mount
    loadChatHistory();
  }, []);

  const loadChatHistory = async () => {
    try {
      const response = await fetch(`${API_URL}/api/chat/history`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      if (response.ok) {
        const history = await response.json();
        setMessages(history);
      }
    } catch (err) {
      console.error('Failed to load chat history:', err);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    setLoading(true);
    try {
      const response = await fetch(`${API_URL}/api/chat`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ message: input })
      });

      if (response.ok) {
        const data = await response.json();
        setMessages(prev => [
          ...prev,
          { role: 'user', message: input },
          { role: 'assistant', message: data.assistant_response }
        ]);
        setInput('');
      }
    } catch (err) {
      console.error('Chat error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="chat-page">
      <h2>Chat with AI Health Assistant</h2>
      <div className="chat-container">
        <div className="chat-messages">
          {messages.length === 0 ? (
            <div className="chat-welcome">
              <p>Hi! I'm your HealMate AI Assistant. Ask me anything about your health, medications, nutrition, or wellness goals.</p>
            </div>
          ) : (
            messages.map((msg, idx) => (
              <div key={idx} className={`chat-message ${msg.role}`}>
                <pre style={{whiteSpace: 'pre-wrap', fontFamily: 'inherit', margin: 0}}>{msg.message}</pre>
              </div>
            ))
          )}
          {loading && <div className="chat-message assistant"><p>Thinking...</p></div>}
        </div>
        <form onSubmit={handleSendMessage} className="chat-form">
          <input
            type="text"
            placeholder="Ask me about your health..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            disabled={loading}
          />
          <button type="submit" className="btn-primary" disabled={loading}>
            Send
          </button>
        </form>
      </div>
    </div>
  );
}

// ==================== EMAIL PAGE ====================

function EmailPage({ token }) {
  const [sending, setSending] = useState(false);
  const [message, setMessage] = useState('');

  const handleSendEmail = async () => {
    setSending(true);
    setMessage('');
    try {
      const response = await fetch(`${API_URL}/api/send-email`, {
        method: 'POST',
        headers: { 'Authorization': `Bearer ${token}` }
      });

      if (response.ok) {
        setMessage('Weekly report sent successfully to your email!');
      } else {
        setMessage('Failed to send report. Please try again.');
      }
    } catch (err) {
      setMessage('Error sending report: ' + err.message);
    } finally {
      setSending(false);
    }
  };

  return (
    <div className="email-page">
      <h2>Email Weekly Report</h2>
      <div className="email-card">
        <h3>Send Your Weekly Health Summary</h3>
        <p>Get a comprehensive overview of your health metrics for the past week delivered to your email.</p>
        
        <div className="email-info">
          <h4>Report includes:</h4>
          <ul>
            <li>Total sleep hours</li>
            <li>Water intake</li>
            <li>Exercise minutes</li>
            <li>Meditation time</li>
            <li>Medication adherence</li>
            <li>Mood and stress levels</li>
            <li>Personalized health tips</li>
          </ul>
        </div>

        {message && <div className={`message ${message.includes('success') ? 'success' : 'error'}`}>{message}</div>}

        <button
          className="btn-primary"
          onClick={handleSendEmail}
          disabled={sending}
          style={{ marginTop: '20px' }}
        >
          {sending ? 'Sending...' : 'Send Weekly Report Now'}
        </button>
      </div>
    </div>
  );
}

// ==================== MAIN DASHBOARD ====================

function MainDashboard({ token, onLogout }) {
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [profile, setProfile] = useState(null);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [schedule, setSchedule] = useState([]);
  const [notificationsEnabled, setNotificationsEnabled] = useState(false);
  
  // Daily goals state
  const [dailyGoals, setDailyGoals] = useState({
    medications: [],
    water: 0,
    meals: { breakfast: false, lunch: false, dinner: false }
  });

  useEffect(() => {
    fetchProfile();
    requestNotificationPermission();
    const timer = setInterval(() => setCurrentTime(new Date()), 60000);
    return () => clearInterval(timer);
  }, [token]);

  useEffect(() => {
    if (profile) {
      generateSchedule();
    }
  }, [profile]);

  useEffect(() => {
    if (schedule.length > 0 && notificationsEnabled) {
      startNotifications();
    }
  }, [schedule, notificationsEnabled]);

  const requestNotificationPermission = async () => {
    if ('Notification' in window) {
      const permission = await Notification.requestPermission();
      setNotificationsEnabled(permission === 'granted');
    }
  };

  const startNotifications = () => {
    // Test notification immediately
    if (notificationsEnabled) {
      showNotification('Notifications are working! You will receive reminders at scheduled times.', 'break');
    }

    const checkSchedule = () => {
      const now = new Date();
      const currentTime = `${now.getHours().toString().padStart(2, '0')}:${now.getMinutes().toString().padStart(2, '0')}`;
      
      console.log('Checking schedule at:', currentTime); // Debug log
      
      schedule.forEach(item => {
        if (item.time === currentTime) {
          console.log('Triggering notification for:', item.activity); // Debug log
          showNotification(item.activity, item.type);
        }
      });
    };

    // Check every 30 seconds (more frequent for demo)
    const interval = setInterval(checkSchedule, 30000);
    checkSchedule(); // Check immediately

    return () => clearInterval(interval);
  };

  const showNotification = (message, type) => {
    if (!notificationsEnabled) return;

    const icons = {
      water: 'ðŸ’§',
      food: 'ðŸ½ï¸',
      medication: 'ðŸ’Š',
      break: 'ðŸ§˜',
      sleep: 'ðŸ˜´'
    };

    const icon = icons[type] || 'â°';
    
    new Notification('HealMate Reminder', {
      body: `${icon} ${message}`,
      icon: '/favicon.ico',
      badge: '/favicon.ico',
      tag: type,
      requireInteraction: false
    });

    // Also play a sound (optional)
    try {
      const audio = new Audio('data:audio/wav;base64,');
      audio.volume = 0.3;
      audio.play().catch(() => {}); // Ignore errors if autoplay is blocked
    } catch (e) {
      // Ignore audio errors
    }
  };

  const fetchProfile = async () => {
    try {
      console.log('Fetching profile with token:', token);
      const response = await fetch(`${API_URL}/api/user/profile`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      console.log('Profile response status:', response.status);
      if (response.ok) {
        const data = await response.json();
        console.log('Profile data:', data);
        setProfile(data);
      } else {
        const error = await response.json();
        console.error('Profile fetch failed:', error);
      }
    } catch (err) {
      console.error('Failed to fetch profile:', err);
    }
  };

  const generateSchedule = () => {
    if (!profile) return;

    const scheduleItems = [];
    const sleepHours = parseInt(profile.sleep_goal_hours) || 8;
    
    // Calculate wake and sleep times
    const wakeTime = 7; // 7 AM wake up
    const bedTime = 22; // 10 PM default bedtime
    const actualBedTime = (wakeTime + 24 - sleepHours) % 24; // Calculate bedtime based on wake time and sleep hours
    const finalBedTime = actualBedTime < 20 ? 22 : actualBedTime; // Use 10 PM if calculated time is too early

    // Add wake up time
    scheduleItems.push({
      time: `${wakeTime}:00`,
      activity: 'â° Wake Up',
      type: 'sleep'
    });

    // Break reminders (every 5 minutes for demo)
    const now = new Date();
    const currentHour = now.getHours();
    const currentMinute = now.getMinutes();
    for (let i = 0; i < 12; i++) { // Next 12 reminders (1 hour worth)
      const nextMinute = (currentMinute + (i * 5)) % 60;
      const nextHour = currentHour + Math.floor((currentMinute + (i * 5)) / 60);
      scheduleItems.push({
        time: `${nextHour.toString().padStart(2, '0')}:${nextMinute.toString().padStart(2, '0')}`,
        activity: 'ðŸ§˜ Take a 5-min Break',
        type: 'break'
      });
    }

    // Breakfast (1 hour after wake)
    const breakfastHour = wakeTime + 1;
    scheduleItems.push({
      time: `${breakfastHour.toString().padStart(2, '0')}:00`,
      activity: 'ðŸ³ Breakfast',
      type: 'food'
    });

    // Lunch
    scheduleItems.push({
      time: '12:30',
      activity: 'ðŸ½ï¸ Lunch',
      type: 'food'
    });

    // Dinner (3 hours before bed)
    const dinnerHour = finalBedTime - 3;
    scheduleItems.push({
      time: `${dinnerHour.toString().padStart(2, '0')}:00`,
      activity: 'ðŸ² Dinner',
      type: 'food'
    });

    // Sleep suggestion
    scheduleItems.push({
      time: `${finalBedTime.toString().padStart(2, '0')}:00`,
      activity: `ðŸ˜´ Sleep Time (${sleepHours}h needed)`,
      type: 'sleep'
    });

    // Add medications
    if (profile.medications && profile.medications.length > 0) {
      profile.medications.forEach(med => {
        scheduleItems.push({
          time: '08:00',
          activity: `ðŸ’Š Take Medication: ${med.name}`,
          type: 'medication'
        });
        // Add evening dose if needed
        scheduleItems.push({
          time: '20:00',
          activity: `ðŸ’Š Take Medication: ${med.name}`,
          type: 'medication'
        });
      });
    }

    // Add water reminders with specific times
    scheduleItems.push({
      time: '10:00',
      activity: 'ðŸ’§ Drink Water (Glass 1)',
      type: 'water'
    });
    scheduleItems.push({
      time: '14:00',
      activity: 'ðŸ’§ Drink Water (Glass 2)',
      type: 'water'
    });
    scheduleItems.push({
      time: '16:00',
      activity: 'ðŸ’§ Drink Water (Glass 3)',
      type: 'water'
    });
    scheduleItems.push({
      time: '18:00',
      activity: 'ðŸ’§ Drink Water (Glass 4)',
      type: 'water'
    });

    // Sort by time
    scheduleItems.sort((a, b) => {
      const [aHour] = a.time.split(':').map(Number);
      const [bHour] = b.time.split(':').map(Number);
      return aHour - bHour;
    });

    setSchedule(scheduleItems);
  };

  const formatDate = (date) => {
    return date.toLocaleDateString('en-GB', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };

  const formatTime12 = (date) => {
    return date.toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true
    });
  };

  if (!profile) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="main-dashboard">
      {/* Sidebar */}
      <aside className="sidebar">
        <div className="sidebar-header">
          <h2>HealMate</h2>
        </div>

        <nav className="sidebar-nav">
          <button 
            className={currentPage === 'dashboard' ? 'nav-btn active' : 'nav-btn'}
            onClick={() => setCurrentPage('dashboard')}
          >
            Dashboard
          </button>
          <button 
            className={currentPage === 'profile' ? 'nav-btn active' : 'nav-btn'}
            onClick={() => setCurrentPage('profile')}
          >
            Profile
          </button>
          <button 
            className={currentPage === 'chat' ? 'nav-btn active' : 'nav-btn'}
            onClick={() => setCurrentPage('chat')}
          >
            Chat
          </button>
          <button 
            className={currentPage === 'daily' ? 'nav-btn active' : 'nav-btn'}
            onClick={() => setCurrentPage('daily')}
          >
            Daily Goals
          </button>
          <button 
            className={currentPage === 'weekly' ? 'nav-btn active' : 'nav-btn'}
            onClick={() => setCurrentPage('weekly')}
          >
            Weekly Data
          </button>
          <button 
            className={currentPage === 'workout' ? 'nav-btn active' : 'nav-btn'}
            onClick={() => setCurrentPage('workout')}
          >
            Workout Plan
          </button>
          <button 
            className={currentPage === 'email' ? 'nav-btn active' : 'nav-btn'}
            onClick={() => setCurrentPage('email')}
          >
            Email Report
          </button>
          <button className="nav-btn logout-nav" onClick={onLogout}>
            Logout
          </button>
        </nav>
      </aside>

      {/* Main Content */}
      <main className="dashboard-content">
        {/* Dashboard Page */}
        {currentPage === 'dashboard' && (
          <div className="dashboard-page">
            <div className="time-section">
              <p className="current-date">{formatDate(currentTime)}</p>
              <p className="current-time">{formatTime12(currentTime)}</p>
            </div>

            <h1 className="welcome-msg">Welcome, {profile.nickname}!</h1>

            <div className="schedule-section">
              <h2>Today's Schedule</h2>
              <table className="schedule-table">
                <thead>
                  <tr>
                    <th>Time</th>
                    <th>Activity</th>
                  </tr>
                </thead>
                <tbody>
                  {schedule.map((item, idx) => (
                    <tr key={idx} className={`schedule-row ${item.type}`}>
                      <td className="time-cell">{item.time}</td>
                      <td className="activity-cell">{item.activity}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {/* Profile Page */}
        {currentPage === 'profile' && (
          <div className="profile-page">
            <h2>Your Profile</h2>
            <div className="profile-card">
              <div className="profile-field">
                <label>Nickname:</label>
                <p>{profile.nickname}</p>
              </div>
              <div className="profile-field">
                <label>Height:</label>
                <p>{profile.height_cm} cm</p>
              </div>
              <div className="profile-field">
                <label>Weight:</label>
                <p>{profile.weight_kg} kg</p>
              </div>
              <div className="profile-field">
                <label>Blood Type:</label>
                <p>{profile.blood_type}</p>
              </div>
              <div className="profile-field">
                <label>Blood Pressure:</label>
                <p>{profile.blood_pressure_sys}/{profile.blood_pressure_dia}</p>
              </div>
              <div className="profile-field">
                <label>Blood Sugar:</label>
                <p>{profile.blood_sugar_fasting} mg/dL</p>
              </div>
            </div>
          </div>
        )}

        {/* Chat Page */}
        {currentPage === 'chat' && (
          <ChatPage token={token} />
        )}

        {/* Daily Goals Page */}
        {currentPage === 'daily' && (
          <div className="daily-page">
            <h2>Daily Goals Tracker</h2>
            
            {/* Medications */}
            <div className="goal-section">
              <h3>ðŸ’Š Medications ({dailyGoals.medications.filter(m => m.taken).length} / {profile.medications?.length || 0})</h3>
              {profile.medications && profile.medications.length > 0 ? (
                <div className="goal-items">
                  {profile.medications.map((med, idx) => (
                    <div key={idx} className="goal-item">
                      <input 
                        type="checkbox" 
                        checked={dailyGoals.medications[idx]?.taken || false}
                        onChange={() => {
                          const newMeds = [...dailyGoals.medications];
                          newMeds[idx] = { name: med.name, taken: !newMeds[idx]?.taken };
                          setDailyGoals({...dailyGoals, medications: newMeds});
                        }}
                      />
                      <label>{med.name} - {med.dosage}</label>
                    </div>
                  ))}
                </div>
              ) : (
                <p>No medications added yet</p>
              )}
            </div>

            {/* Water Intake */}
            <div className="goal-section">
              <h3>ðŸ’§ Water Intake ({dailyGoals.water} / 8 glasses)</h3>
              <div className="water-tracker">
                {[...Array(8)].map((_, i) => (
                  <button
                    key={i}
                    className={`water-glass ${i < dailyGoals.water ? 'filled' : ''}`}
                    onClick={() => setDailyGoals({...dailyGoals, water: i + 1})}
                  >
                    {i < dailyGoals.water ? 'ðŸ’§' : 'ðŸ¥¤'}
                  </button>
                ))}
              </div>
              <button 
                className="btn-reset-water"
                onClick={() => setDailyGoals({...dailyGoals, water: 0})}
              >
                Reset
              </button>
            </div>

            {/* Meals */}
            <div className="goal-section">
              <h3>ðŸ½ï¸ Meals ({Object.values(dailyGoals.meals).filter(m => m).length} / 3)</h3>
              <div className="goal-items">
                <div className="goal-item">
                  <input 
                    type="checkbox" 
                    checked={dailyGoals.meals.breakfast}
                    onChange={() => setDailyGoals({
                      ...dailyGoals, 
                      meals: {...dailyGoals.meals, breakfast: !dailyGoals.meals.breakfast}
                    })}
                  />
                  <label>ðŸ³ Breakfast</label>
                </div>
                <div className="goal-item">
                  <input 
                    type="checkbox" 
                    checked={dailyGoals.meals.lunch}
                    onChange={() => setDailyGoals({
                      ...dailyGoals, 
                      meals: {...dailyGoals.meals, lunch: !dailyGoals.meals.lunch}
                    })}
                  />
                  <label>ðŸ½ï¸ Lunch</label>
                </div>
                <div className="goal-item">
                  <input 
                    type="checkbox" 
                    checked={dailyGoals.meals.dinner}
                    onChange={() => setDailyGoals({
                      ...dailyGoals, 
                      meals: {...dailyGoals.meals, dinner: !dailyGoals.meals.dinner}
                    })}
                  />
                  <label>ðŸ² Dinner</label>
                </div>
              </div>
            </div>

            {/* Progress Summary */}
            <div className="goal-summary">
              <h3>ðŸ“Š Today's Progress</h3>
              <div className="progress-bars">
                <div className="progress-item">
                  <label>Overall Completion</label>
                  <div className="progress-bar">
                    <div 
                      className="progress-fill" 
                      style={{
                        width: `${((dailyGoals.medications.filter(m => m.taken).length / (profile.medications?.length || 1)) * 33 + 
                               (dailyGoals.water / 8) * 33 + 
                               (Object.values(dailyGoals.meals).filter(m => m).length / 3) * 34)}%`
                      }}
                    ></div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Weekly Data Page */}
        {currentPage === 'weekly' && (
          <div className="weekly-page">
            <h2>Weekly Data</h2>
            <p>Weekly analytics coming soon...</p>
          </div>
        )}

        {/* Workout Page */}
        {currentPage === 'workout' && (
          <div className="workout-page">
            <h2>AI Workout Plan</h2>
            <p>Personalized workout plan coming soon...</p>
          </div>
        )}

        {/* Email Report Page */}
        {currentPage === 'email' && (
          <EmailPage token={token} />
        )}
      </main>
    </div>
  );
}

// ==================== MAIN APP ====================

export default function App() {
  // Debug logging for localStorage state
  const storedToken = localStorage.getItem('token');
  const storedProfileSetup = localStorage.getItem('profileSetup');
  
  console.log('App mount:', {
    token: storedToken,
    profileSetup: storedProfileSetup,
    isLoggedIn: !!storedToken
  });
  
  const [showLanding, setShowLanding] = useState(!storedToken); // Show landing only if not logged in
  const [isLoggedIn, setIsLoggedIn] = useState(!!storedToken);
  const [token, setToken] = useState(storedToken);
  const [profileSetup, setProfileSetup] = useState(storedProfileSetup === 'true');

  const handleGetStarted = () => {
    setShowLanding(false);
  };

  const handleLogin = () => {
    const newToken = localStorage.getItem('token');
    setToken(newToken);
    setIsLoggedIn(true);
    setShowLanding(false); // Hide landing page after login
    // Reset profileSetup on login to force profile setup for new users
    const setup = localStorage.getItem('profileSetup') === 'true';
    setProfileSetup(setup);
    console.log('Login:', { token: newToken, profileSetup: setup });
  };

  const handleProfileComplete = () => {
    // Only set profileSetup after successful profile update
    localStorage.setItem('profileSetup', 'true');
    setProfileSetup(true);
    console.log('Profile setup complete, redirecting to dashboard');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
    localStorage.removeItem('profileSetup');
    setIsLoggedIn(false);
    setToken(null);
    setProfileSetup(false);
    console.log('Logout: cleared localStorage');
  };

  if (!isLoggedIn) {
    console.log('Rendering LoginPage');
    return <LoginPage onLogin={handleLogin} />;
  }

  if (!profileSetup) {
    console.log('Rendering ProfileSetupPage');
    return <ProfileSetupPage token={token} onComplete={handleProfileComplete} />;
  }

  console.log('Rendering MainDashboard');
  return <MainDashboard token={token} onLogout={handleLogout} />;
}
