// Daily Check-In Component
function DailyCheckInView({ token, onComplete }) {
  const [formData, setFormData] = useState({
    mood: '',
    stress_level: 5,
    sleep_hours: '',
    water_intake_liters: '',
    exercise_minutes: '',
    meditation_minutes: '',
    notes: ''
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'stress_level' ? parseInt(value) : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      const response = await fetch('http://localhost:5000/api/health-checkin', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(formData)
      });
      
      if (response.ok) {
        setMessage('âœ… Check-in saved successfully!');
        onComplete();
        setTimeout(() => setMessage(''), 3000);
      } else {
        const error = await response.json();
        setMessage(error.error);
      }
    } catch (err) {
      setMessage('Error saving check-in');
    }
  };

  return (
    <div className="checkin-view">
      <h2>Daily Health Check-In</h2>
      
      {message && <div className="message">{message}</div>}
      
      <form onSubmit={handleSubmit} className="checkin-form">
        <div className="form-group">
          <label>How are you feeling?</label>
          <select name="mood" value={formData.mood} onChange={handleChange} required>
            <option value="">Select mood</option>
            <option value="happy">ðŸ˜Š Happy</option>
            <option value="neutral">ðŸ˜ Neutral</option>
            <option value="sad">ðŸ˜¢ Sad</option>
            <option value="anxious">ðŸ˜° Anxious</option>
            <option value="energetic">âš¡ Energetic</option>
            <option value="tired">ðŸ˜´ Tired</option>
          </select>
        </div>

        <div className="form-group">
          <label>Stress Level (1-10)</label>
          <input
            type="range"
            name="stress_level"
            min="1"
            max="10"
            value={formData.stress_level}
            onChange={handleChange}
          />
          <span className="value">{formData.stress_level}</span>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>Sleep Hours</label>
            <input
              type="number"
              name="sleep_hours"
              placeholder="Hours"
              value={formData.sleep_hours}
              onChange={handleChange}
              step="0.5"
            />
          </div>

          <div className="form-group">
            <label>Water Intake (Liters)</label>
            <input
              type="number"
              name="water_intake_liters"
              placeholder="Liters"
              value={formData.water_intake_liters}
              onChange={handleChange}
              step="0.5"
            />
          </div>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>Exercise (Minutes)</label>
            <input
              type="number"
              name="exercise_minutes"
              placeholder="Minutes"
              value={formData.exercise_minutes}
              onChange={handleChange}
            />
          </div>

          <div className="form-group">
            <label>Meditation (Minutes)</label>
            <input
              type="number"
              name="meditation_minutes"
              placeholder="Minutes"
              value={formData.meditation_minutes}
              onChange={handleChange}
            />
          </div>
        </div>

        <div className="form-group">
          <label>Notes</label>
          <textarea
            name="notes"
            placeholder="Any additional notes about your health today..."
            value={formData.notes}
            onChange={handleChange}
          />
        </div>

        <button type="submit" className="btn-primary btn-large">
          Save Check-in
        </button>
      </form>
    </div>
  );
}

// Medications Component
function MedicationsView({ token }) {
  const [medications, setMedications] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newMed, setNewMed] = useState({
    name: '',
    dosage: '',
    frequency: '',
    reason: '',
    stock_quantity: '',
    refill_threshold: '10'
  });

  useEffect(() => {
    fetchMedications();
  }, [token]);

  const fetchMedications = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/medications', {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      if (response.ok) {
        const data = await response.json();
        setMedications(data);
      }
    } catch (err) {
      console.error('Fetch error:', err);
    }
  };

  const handleAddMedication = async (e) => {
    e.preventDefault();
    
    try {
      const response = await fetch('http://localhost:5000/api/medications', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(newMed)
      });
      
      if (response.ok) {
        setNewMed({
          name: '',
          dosage: '',
          frequency: '',
          reason: '',
          stock_quantity: '',
          refill_threshold: '10'
        });
        setShowForm(false);
        fetchMedications();
      }
    } catch (err) {
      console.error('Error adding medication:', err);
    }
  };

  const handleLogIntake = async (medId) => {
    try {
      await fetch(`http://localhost:5000/api/medications/${medId}/intake`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ skipped: false })
      });
      fetchMedications();
    } catch (err) {
      console.error('Error logging intake:', err);
    }
  };

  return (
    <div className="medications-view">
      <h2>Medications</h2>
      
      <button 
        className="btn-primary"
        onClick={() => setShowForm(!showForm)}
      >
        {showForm ? 'Cancel' : '+ Add Medication'}
      </button>

      {showForm && (
        <form onSubmit={handleAddMedication} className="medication-form">
          <input
            type="text"
            placeholder="Medication Name"
            value={newMed.name}
            onChange={(e) => setNewMed({...newMed, name: e.target.value})}
            required
          />
          
          <input
            type="text"
            placeholder="Dosage (e.g., 500mg)"
            value={newMed.dosage}
            onChange={(e) => setNewMed({...newMed, dosage: e.target.value})}
          />
          
          <input
            type="text"
            placeholder="Frequency (e.g., twice daily)"
            value={newMed.frequency}
            onChange={(e) => setNewMed({...newMed, frequency: e.target.value})}
            required
          />
          
          <input
            type="text"
            placeholder="Reason"
            value={newMed.reason}
            onChange={(e) => setNewMed({...newMed, reason: e.target.value})}
          />
          
          <input
            type="number"
            placeholder="Stock Quantity"
            value={newMed.stock_quantity}
            onChange={(e) => setNewMed({...newMed, stock_quantity: e.target.value})}
            required
          />
          
          <button type="submit" className="btn-primary">Add Medication</button>
        </form>
      )}

      <div className="medications-list">
        {medications.map(med => (
          <div key={med.id} className="medication-card">
            <div className="med-header">
              <h4>{med.name}</h4>
              <span className={`stock ${med.stock_quantity < med.refill_threshold ? 'low' : ''}`}>
                Stock: {med.stock_quantity}
              </span>
            </div>
            <p><strong>Dosage:</strong> {med.dosage}</p>
            <p><strong>Frequency:</strong> {med.frequency}</p>
            <p><strong>Reason:</strong> {med.reason}</p>
            <button 
              className="btn-secondary"
              onClick={() => handleLogIntake(med.id)}
            >
              âœ“ Log Intake
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

// Chat Component
function ChatView({ token }) {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchChatHistory();
  }, [token]);

  const fetchChatHistory = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/chat/history', {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      if (response.ok) {
        const data = await response.json();
        setMessages(data);
      }
    } catch (err) {
      console.error('Fetch error:', err);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    setLoading(true);
    
    try {
      const response = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ message: input })
      });
      
      if (response.ok) {
        const data = await response.json();
        setMessages(prev => [
          ...prev,
          { role: 'user', message: data.user_message, id: Date.now() },
          { role: 'assistant', message: data.assistant_response, id: Date.now() + 1 }
        ]);
        setInput('');
      }
    } catch (err) {
      console.error('Error sending message:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="chat-view">
      <h2>Chat with HealthPal Assistant</h2>
      
      <div className="chat-messages">
        {messages.map(msg => (
          <div key={msg.id} className={`message ${msg.role}`}>
            <div className="message-content">
              {msg.message}
            </div>
          </div>
        ))}
        {loading && <div className="message assistant"><span className="typing">Thinking...</span></div>}
      </div>

      <form onSubmit={handleSendMessage} className="chat-input-form">
        <input
          type="text"
          placeholder="Ask me about your health..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          disabled={loading}
        />
        <button type="submit" disabled={loading} className="btn-primary">
          Send
        </button>
      </form>
    </div>
  );
}

// Profile View Component
function ProfileView({ token }) {
  const [profile, setProfile] = useState(null);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});

  useEffect(() => {
    fetchProfile();
  }, [token]);

  const fetchProfile = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/user/profile', {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      if (response.ok) {
        const data = await response.json();
        setProfile(data);
        setFormData(data);
      }
    } catch (err) {
      console.error('Fetch error:', err);
    }
  };

  const handleSaveProfile = async (e) => {
    e.preventDefault();
    
    try {
      const response = await fetch('http://localhost:5000/api/user/profile', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(formData)
      });
      
      if (response.ok) {
        setEditing(false);
        fetchProfile();
      }
    } catch (err) {
      console.error('Error saving profile:', err);
    }
  };

  if (!profile) return <div className="loading">Loading profile...</div>;

  return (
    <div className="profile-view">
      <h2>Your Health Profile</h2>
      
      <button 
        className="btn-primary"
        onClick={() => setEditing(!editing)}
      >
        {editing ? 'Cancel' : 'âœï¸ Edit Profile'}
      </button>

      {editing ? (
        <form onSubmit={handleSaveProfile} className="profile-form">
          <h3>Personal Information</h3>
          <input
            type="text"
            placeholder="Name"
            value={formData.name || ''}
            onChange={(e) => setFormData({...formData, name: e.target.value})}
          />
          <input
            type="number"
            placeholder="Age"
            value={formData.age || ''}
            onChange={(e) => setFormData({...formData, age: e.target.value})}
          />
          
          <h3>Health Measurements</h3>
          <input
            type="number"
            placeholder="Height (cm)"
            value={formData.height_cm || ''}
            onChange={(e) => setFormData({...formData, height_cm: e.target.value})}
          />
          <input
            type="number"
            placeholder="Weight (kg)"
            value={formData.weight_kg || ''}
            onChange={(e) => setFormData({...formData, weight_kg: e.target.value})}
          />
          
          <button type="submit" className="btn-primary">Save Changes</button>
        </form>
      ) : (
        <div className="profile-display">
          <div className="profile-section">
            <h3>Personal Information</h3>
            <p><strong>Name:</strong> {profile.name}</p>
            <p><strong>Email:</strong> {profile.email}</p>
            <p><strong>Age:</strong> {profile.age || 'Not set'}</p>
            <p><strong>Job:</strong> {profile.job_title || 'Not set'}</p>
          </div>

          <div className="profile-section">
            <h3>Health Measurements</h3>
            <p><strong>Height:</strong> {profile.height_cm} cm</p>
            <p><strong>Weight:</strong> {profile.weight_kg} kg</p>
            <p><strong>Blood Type:</strong> {profile.blood_type || 'Not set'}</p>
            <p><strong>Blood Pressure:</strong> {profile.blood_pressure}</p>
          </div>

          <div className="profile-section">
            <h3>Health Goals</h3>
            <p><strong>Sleep Goal:</strong> {profile.sleep_goal_hours} hours</p>
            <p><strong>Exercise Goal:</strong> {profile.exercise_goal_minutes} minutes</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [userId, setUserId] = useState(localStorage.getItem('userId'));
  const [showProfile, setShowProfile] = useState(false);

  const handleLogin = () => {
    setToken(localStorage.getItem('token'));
    setUserId(localStorage.getItem('userId'));
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
    setToken(null);
    setUserId(null);
    setIsLoggedIn(false);
    setShowProfile(false);
  };

  if (!isLoggedIn && !token) {
    return <LoginPage onLogin={handleLogin} />;
  }

  return (
    <div>
      {showProfile ? (
        <ProfileSetup token={token} userId={userId} onComplete={() => setShowProfile(false)} />
      ) : (
        <Dashboard token={token} userId={userId} onLogout={handleLogout} />
      )}
    </div>
  );
}
